let command = msg.payload.command;

// Регулярка для удаления командных слов
const regex = /^(?:включи(ть)?\s+)?(музыку|play music|трек|подкаст|стендап)\s*/i;

// Удаляем командную часть
let searchQuery = command.replace(regex, "").trim();

// Если запрос пустой, подставляем случайный стиль музыки или категорию
if (!searchQuery) {
    const randomGenres = ["rock", "jazz", "lofi", "electronic", "hip-hop", "blues"];
    searchQuery = `${randomGenres[Math.floor(Math.random() * randomGenres.length)]} track`;
}

msg.payload = `ytsearch1:${searchQuery}`;
msg.originalPayload = `ytsearch1:${searchQuery}`;

return msg;

msg.basePath = "/tmp";
msg.baseName = "music";
msg.extension = ".mp3";
msg.counter = 0;
msg.filePath = `${msg.basePath}/${msg.baseName}${msg.extension}`;
return msg;

let command = msg.command.toLowerCase();
let regex = /^(включи(ть)? радио|turn on radio)\s*(\d+|.+)?$/i;
let match = command.match(regex);

if (match) {
    let stationIdentifier = match[3] ? match[3].trim() : "";
    let stationUrl = msg.defaultStationUrl;

    console.log(stationIdentifier, parseInt(stationIdentifier), !isNaN(parseInt(stationIdentifier)))
    if (stationIdentifier) {
        if (!isNaN(parseInt(stationIdentifier))) {
            // Если stationIdentifier - число, выбираем станцию по номеру
            let stationIndex = parseInt(stationIdentifier, 10) - 1;
            let stationKeys = Object.keys(msg.stations);
            if (stationIndex >= 0 && stationIndex < stationKeys.length) {
                let stationName = stationKeys[stationIndex];
                stationUrl = msg.stations[stationName];
            }
            console.log(stationUrl)
        } else {
            // Если stationIdentifier - текст, выбираем станцию по названию
            let lowerIdentifier = stationIdentifier.toLowerCase();
            for (let [name, url] of Object.entries(msg.stations)) {
                if (name.toLowerCase() === lowerIdentifier) {
                    stationUrl = url;
                    break;
                }
            }
        }
    }

    msg.stationUrl = stationUrl;
    global.set("currentPlayback", "radio");
    return msg;
}

let command = msg.payload.command.toLowerCase();
if (/сделай (радио|музыку) тише/i.test(command)) {
    msg.volumeCommand = "amixer set Master 10%-";
}
if (command.includes("всю громкость") || command.includes("на всю мощь") || command.includes("максимальную громкость")) {
    msg.volumeCommand = "amixer set Master 100%";
}
if (/сделай (радио|музыку) громче/i.test(command)) {
    msg.volumeCommand = "amixer set Master 10%+";
}
if (command.includes("выключи звук") || command.includes("тихо")) {
    msg.volumeCommand = "amixer set Master 0%";
}
return msg;
